                OUTPUT INSERTED.[tid_Course]
                , INSERTED.[CourseCode]
                , INSERTED.[EducationOrganizationId]
                , INSERTED.[LastModifiedDate]
                , INSERTED.[Id]
                INTO @TidsChild;

            INSERT INTO [t_edfi].[CourseSnapshotRecord]
            (
