                    , [ClassroomIdentificationCode]
                    , [LocalCourseCode]
                    , [SchoolId]
                    , [SchoolYear]
                    , [SequenceOfCourse]
                    , [TermDescriptorCodeValue]
                    , [UniqueSectionCode]
                    , [EducationalEnvironmentType]
                    , [MediumOfInstructionType]
