                SET @Message = FORMATMESSAGE(50009, CAST(@pEndDate AS NVARCHAR(15)), CAST(@pBeginDate AS NVARCHAR(15)));
                THROW 50009, @Message, 1;
            END;

            -- end date overlaps other snapshot
