            (
                SELECT [SnapshotId]
                FROM [tods].[Snapshot]
                WHERE @pEndDate > [EffectiveBeginDate]
                AND
                (
                    @pEndDate <= COALESCE([EffectiveEndDate], DATEADD(DAY, 1, [SnapshotDate]))
