            UPDATE [tods].[SnapshotCode] SET [t_PartitionYearId] = @PartitionYearId WHERE [SnapshotId] = @SnapshotId;


            UPDATE [t_edfi].[AcademicSubjectDescriptorSnapshotRecord]
