    BEGIN CATCH
        IF (XACT_STATE()) <> 0
            BEGIN
                ROLLBACK TRANSACTION;
            END;

        BEGIN TRANSACTION
            INSERT INTO [tods].[AuditLog]
            (
                ErrorNumber
                , ErrorMessage
                , ErrorSeverity
                , SqlErrorSeverity
                , ErrorState
                , ErrorLine
                , ErrorSproc
                , SnapshotId
            )
            SELECT
                (CAST(ERROR_NUMBER() AS VARCHAR(10)))
                , ERROR_MESSAGE()
                , 'ERROR'
                , ERROR_SEVERITY()
                , CAST(ERROR_STATE() AS VARCHAR(10))
                , CAST(ERROR_LINE() AS VARCHAR(10))
                , @SprocName
                , @SnapshotId
        COMMIT TRANSACTION;

        THROW;
    END CATCH;
